EVENT my_ude
{
  POST_EVENT "my_ude"
  UI_LABEL "My Ude"
  CATEGORY Mill Drill
  PARAM group_start_1
  {
    TYPE g
    DEFVAL    "start_open"
    UI_LABEL  "My Parameters"
  }
    PARAM int_param
    {
      TYPE i
      DEFVAL "0”
      TOGGLE On
      UI_LABEL "Integer Parameter"
    }
    PARAM option_param
    {
      TYPE o
      DEFVAL "Option_2"
      OPTIONS "Option_1","Option_2","Option_3"
      UI_LABEL "Option Parameter"
    }
    PARAM double_param
    {
      TYPE d
      DEFVAL "5.0"
      UI_LABEL "Double Parameter"
    }
    PARAM point_param
    {
      TYPE p
      UI_LABEL "Point Parameter"
    }
  PARAM group_end_1
  {
    TYPE g
    DEFVAL  "end"
  }
